    static void getPlayerPriceList() {
        def jsonSlurper = new JsonSlurper()

        def result = jsonSlurper.parseText(getPlayerInfoFromJSONString())

        ArrayList<Player> playerList = new ArrayList<>()

        result.playerList.each { it ->

            def player = new Player(name: it.fn + " " + it.ln ,
                    position: it.pn,
                    price: it.s)

            if(it.tid == it.htid) {
                player.teamAbbreviation = it.htabbr
            } else {
                player.teamAbbreviation = it.atabbr
            }
            playerList.add(player)

        }

        def outFile  = new File("d:\\salaries.csv")
        outFile.withWriter('UTF-8'){ writer ->
            writer.write("Name,Position,Team,Salary\r\n")
            playerList.each { player ->
                writer.write("$player.name,$player.position,$player.teamAbbreviation,$player.price\r\n")
            }
        }

        System.out.println("Number Of Players in Player List = $playerList.size")
    }

    static void getPlayerStatList() {
        def jsonSlurper = new JsonSlurper()

        def result = jsonSlurper.parseText(getPlayerInfoFromNBAStatsSite())

        ArrayList<Player> playerList = new ArrayList<>()

        result.resultSets[0].rowSet.each { it ->

            playerList.add(new Player(id: it[0],
                    name: it[1],
                    onCurrentRoster: it[2],
                    fromYear: it[3],
                    toYear: it[4],
                    playerCode: it[5],
                    teamId: it[6],
                    teamCity: it[7],
                    teamName: it[8],
                    teamAbbreviation: it[9],
                    teamCode: it[10],
                    gamesPlayedFlag: it[11]
            ))
        }

        System.out.println("Number Of Players in Player List = $playerList.size")
    }

    static String getPlayerInfoFromNBAStatsSite() {

        CloseableHttpClient httpclient = HttpClients.createDefault();
        try {
            HttpGet httpget = new HttpGet("http://stats.nba.com/stats/commonallplayers?IsOnlyCurrentSeason=1&LeagueID=00&Season=2015-16");

            System.out.println("Executing request " + httpget.getRequestLine());

            // Create a custom response handler
            ResponseHandler<String> responseHandler = new ResponseHandler<String>() {

                @Override
                public String handleResponse(
                        final HttpResponse response) throws ClientProtocolException, IOException {
                    int status = response.getStatusLine().getStatusCode();
                    if (status >= 200 && status < 300) {
                        HttpEntity entity = response.getEntity();
                        return entity != null ? EntityUtils.toString(entity) : null;
                    } else {
                        throw new ClientProtocolException("Unexpected response status: " + status);
                    }
                }

            };
            String responseBody = httpclient.execute(httpget, responseHandler);
            return responseBody
        } finally {
            httpclient.close();
        }


    }

